class Vtiger_CkEditor_Js{constructor(element,params){if(typeof element!=="undefined"){this.loadCkEditor(element,params)}}setElement(element){this.element=$(element);return this}getElement(){return this.element}getElementId(){return this.getElement().attr("id")}getCkEditorInstanceFromName(){return CKEDITOR.instances[this.getElementId()]}getPlainText(){return this.getCkEditorInstanceFromName().document.getBody().getText()}loadCkEditor(element,customConfig){element=$(element).get(0);this.setElement(element);const instance=this.getCkEditorInstanceFromName();let config={on:{instanceReady:function(evt){evt.editor.on("blur",function(){evt.editor.updateElement()})}}};if(typeof customConfig!=="undefined"){config=$.extend(config,customConfig)}if(instance){CKEDITOR.remove(instance)}CKEDITOR.replace(element,config)}loadContentsInCkeditor(contents){const editor=this.getCkEditorInstanceFromName();editor.setData(editor.getData().replace(editorData,contents))}}